import React from 'react';
import { connect } from 'react-redux';
import { fetchStreams } from '../../actions'

class StreamFind extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            searchTerm: '',
            searchResults: null
        }
    }

    componentDidMount() {
        this.props.fetchStreams();
        const { term } = this.props.match.params;
        this.setState({ searchTerm: term })
    }


    render() {
        if(this.props.streams == {}) {
            return <div>find</div>
        }
        let results = [];
        console.log(this.props.streams);
        for(const [key, value] of Object.entries(this.props.streams)) {
            const title = value.title;
            if(title.indexOf(this.state.searchTerm) !== -1) {
                results.push(value);
            } else if(value.username === this.state.searchTerm) {
                results.push(value);
            }
            console.log(value.title + ':' + value.title.indexOf(this.state.searchTerm));
        }
        console.log(results);
        return <div>lnas</div>
    }
}

const mapStateToProps = (state) => {
    return {
        streams: state.streams
    }
}

export default connect(mapStateToProps, { fetchStreams })(StreamFind);
